import React, { useState, useEffect } from 'react';
import { Box, Container, Grid, Typography, IconButton } from '@mui/material';
import { createTheme, ThemeProvider } from '@mui/material/styles';
import { Brightness4, Brightness7 } from '@mui/icons-material'; // Material UI icons for theme toggle
import Search from './components/Search/Search';
import WeeklyForecast from './components/WeeklyForecast/WeeklyForecast';
import TodayWeather from './components/TodayWeather/TodayWeather';
import { fetchWeatherData } from './api/OpenWeatherService';
import { transformDateFormat } from './utilities/DatetimeUtils';
import UTCDatetime from './components/Reusable/UTCDatetime';
import LoadingBox from './components/Reusable/LoadingBox';
import Logo from './assets/logo.png';
import ErrorBox from './components/Reusable/ErrorBox';
import WindyEmbed from './components/Reusable/WindyEmbed';
import { ALL_DESCRIPTIONS } from './utilities/DateConstants';
import {
  getTodayForecastWeather,
  getWeekForecastWeather,
} from './utilities/DataUtils';

function App() {
  // State for theme mode, weather data, and geolocation
  const [themeMode, setThemeMode] = useState('light');
  const [todayWeather, setTodayWeather] = useState(null);
  const [todayForecast, setTodayForecast] = useState([]);
  const [weekForecast, setWeekForecast] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(false);
  const [userLocation, setUserLocation] = useState(null);

  useEffect(() => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          const { latitude, longitude } = position.coords;
          setUserLocation({ latitude, longitude });
          fetchWeatherForLocation(latitude, longitude); // Fetch weather when geolocation is available
        },
        (error) => {
          setError(true);
          console.log(error.message);
        }
      );
    } else {
      setError(true); // If geolocation is not available
    }
  }, []);

  const fetchWeatherForLocation = async (latitude, longitude) => {
    setIsLoading(true);
    const currentDate = transformDateFormat();
    const date = new Date();
    let dt_now = Math.floor(date.getTime() / 1000);

    try {
      const [todayWeatherResponse, weekForecastResponse] =
        await fetchWeatherData(latitude, longitude);
      const all_today_forecasts_list = getTodayForecastWeather(
        weekForecastResponse,
        currentDate,
        dt_now
      );

      const all_week_forecasts_list = getWeekForecastWeather(
        weekForecastResponse,
        ALL_DESCRIPTIONS
      ).slice(0, 7); // Limit to 7 days

      setTodayForecast([...all_today_forecasts_list]);
      setTodayWeather({ city: `Lat: ${latitude}, Lon: ${longitude}`, ...todayWeatherResponse });
      setWeekForecast({
        city: `Lat: ${latitude}, Lon: ${longitude}`,
        list: all_week_forecasts_list,
      });
    } catch (error) {
      setError(true);
    }
    setIsLoading(false);
  };

  const searchChangeHandler = async (enteredData) => {
    const [latitude, longitude] = enteredData.value.split(' ');
    setIsLoading(true);

    const currentDate = transformDateFormat();
    const date = new Date();
    let dt_now = Math.floor(date.getTime() / 1000);

    try {
      const [todayWeatherResponse, weekForecastResponse] =
        await fetchWeatherData(latitude, longitude);
      const all_today_forecasts_list = getTodayForecastWeather(
        weekForecastResponse,
        currentDate,
        dt_now
      );

      const all_week_forecasts_list = getWeekForecastWeather(
        weekForecastResponse,
        ALL_DESCRIPTIONS
      ).slice(0, 7); // Limit to 7 days

      setTodayForecast([...all_today_forecasts_list]);
      setTodayWeather({ city: enteredData.label, ...todayWeatherResponse });
      setWeekForecast({
        city: enteredData.label,
        list: all_week_forecasts_list,
      });
    } catch (error) {
      setError(true);
    }
    setIsLoading(false);
  };

  // Create a theme based on the current themeMode state
  const theme = createTheme({
    palette: {
      mode: themeMode,
      primary: {
        main: themeMode === 'light' ? '#1976d2' : '#90caf9',
      },
      background: {
        default: themeMode === 'light' ? '#ffffff' : '#121212',
        paper: themeMode === 'light' ? '#f4f6f8' : '#1c1c1c',
      },
    },
  });

  // Toggle theme between light and dark
  const toggleTheme = () => {
    setThemeMode(themeMode === 'light' ? 'dark' : 'light');
  };

  let appContent = (
    <Box
      xs={12}
      display="flex"
      flexDirection="column"
      alignItems="center"
      justifyContent="center"
      sx={{
        width: '100%',
        minHeight: '500px',
      }}
    >
      <Typography
        variant="h4"
        component="h4"
        sx={{
          fontSize: { xs: '12px', sm: '14px' },
          color: 'rgba(255,255,255, .85)',
          fontFamily: 'Poppins',
          textAlign: 'center',
          margin: '2rem 0',
          maxWidth: '80%',
          lineHeight: '22px',
        }}
      >
        Explore current weather data and 7-day forecast of more than 200,000
        cities!
      </Typography>
    </Box>
  );

  if (todayWeather && todayForecast && weekForecast) {
    appContent = (
      <React.Fragment>
        <Grid item xs={12} md={todayWeather ? 6 : 12}>
          <Grid item xs={12}>
            <TodayWeather data={todayWeather} forecastList={todayForecast} />
          </Grid>
        </Grid>
        <Grid item xs={12} md={6}>
          <WeeklyForecast data={weekForecast} />
        </Grid>
      </React.Fragment>
    );
  }

  if (error) {
    appContent = (
      <ErrorBox
        margin="3rem auto"
        flex="inherit"
        errorMessage="Something went wrong"
      />
    );
  }

  if (isLoading) {
    appContent = (
      <Box
        sx={{
          display: 'flex',
          justifyContent: 'center',
          alignItems: 'center',
          width: '100%',
          minHeight: '500px',
        }}
      >
        <LoadingBox value="1">
          <Typography
            variant="h3"
            component="h3"
            sx={{
              fontSize: { xs: '10px', sm: '12px' },
              color: 'rgba(255, 255, 255, .8)',
              lineHeight: 1,
              fontFamily: 'Poppins',
            }}
          >
            Loading...
          </Typography>
        </LoadingBox>
      </Box>
    );
  }

  return (
    <ThemeProvider theme={theme}>
      <Container
        sx={{
          maxWidth: { xs: '95%', sm: '80%', md: '90%' },
          width: '100%',
          height: '100%',
          margin: '0 auto',
          padding: '1rem 0 2rem',
          marginBottom: '1rem',
          borderRadius: {
            xs: 'none',
            sm: '0 0 1rem 1rem',
          },
          boxShadow: {
            xs: 'none',
            sm: 'rgba(0,0,0, 0.5) 0px 10px 15px -3px, rgba(0,0,0, 0.5) 0px 4px 6px -2px',
          },
        }}
      >
        <Grid container columnSpacing={2}>
          <Grid item xs={12}>
            <Box
              display="flex"
              justifyContent="space-between"
              alignItems="center"
              sx={{
                width: '100%',
                marginBottom: '1rem',
              }}
            >
              <Box
                component="img"
                sx={{
                  height: { xs: '46px', sm: '52px', md: '56px' },
                  width: 'auto',
                }}
                alt="logo"
                src={Logo}
              />

              <UTCDatetime />

              <IconButton onClick={toggleTheme} color="inherit">
                {themeMode === 'light' ? (
                  <Brightness4 />
                ) : (
                  <Brightness7 />
                )}
              </IconButton>
            </Box>
            <Search onSearchChange={searchChangeHandler} />
          </Grid>
          {appContent}
        </Grid>
        <Box sx={{ marginTop: '3rem' }}>
          <WindyEmbed />
        </Box>
      </Container>
    </ThemeProvider>
  );
}

export default App;
